mapper.update:
UPDATE tblboard
SET title=#{title}, contents=#{contents}
WHERE bno=#{bno}

UPDATE tblboard
SET viewcnt = viewcnt+1
WHERE bno=#{bno}

UPDATE tblboard 
SET goodcnt = goodcnt + 1
WHERE bno = #{bno}

mapper.select:
SELECT * FROM ( SELECT rownum rnum, a.* FROM ( SELECT * FROM tblboard
ORDER BY bno
ORDER BY viewcnt
ORDER BY writer
ORDER BY title
) a ) WHERE rnum BETWEEN #{pageStart} AND #{perPageNum}

SELECT filename
FROM tblboard
WHERE bno=#{bno}

mapper.update:
